import { Component,Input } from '@angular/core';
import { products, Product } from '../products';
import {DialogModule} from 'primeng/dialog';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  products = products;
  @Input() product!:Product;

  share() {
    window.alert('El producto a sido compartido con exito');
  }

  onNotify() {
    window.alert('Se dara una notificacion cuando este se venda');
  }

  display: boolean = false;

    showDialog() {
        this.display = true;
    }
}
<h3>
  <a [title]="product.name + ' details'">
    {{ product.name }}
  </a>
</h3>
<p-dialog [(visible)]="display" [breakpoints]="{'960px': '75vw', '640px':
  '100vw'}" [style]="{width: '50vw'}">
  Contenido
  <br>
  <img src="./assets/phones/{{product.images}}" width="100px" height="100px"
    alt="Sin imagen">
</p-dialog>

<button type="button" (click)="showDialog()" icon="pi pi-info-circle"
  label="Show"></button>



<p *ngIf="product.description">Description: {{ product.description }}</p>
<button type="button" (click)="share()">Share</button>

<app-product-alerts [product]="product" (notify)="onNotify()">></app-product-alerts>
